package IfElseLoop;

public class Learningifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean isMale = false;
		String name = "Bob";
		
		if(isMale) {
			System.out.println("Mr." + name);
		}else
		{
			System.out.println("Ms." + name);
		}
		

	}

}
