package Loops;

import java.util.Scanner;

public class MultiplicationUsingFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner input = new Scanner(System.in);
		int num = 5;
		for (int i=1;i<=10;i++)
		System.out.println(num + "X" + i + "=" + (num*i));
		

	}

}
