package operators;

public class Arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 8;
		int b = 5;
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
		double x = 5;
		double y = 3;
		
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);

	}

}
